<!-- <script type="text/javascript" src="http://code.jquery.com/jquery-1.4.4.min.js"></script> --><script type="text/javascript" src="../jquery.js"></script><script type="text/javascript">	jQuery(document).ready(function(){						// when any option from country list is selected		jQuery("select[name='country']").change(function(){										// get the selected option value of country			var optionValue = jQuery("select[name='country']").val();											/**			 * pass country value through POST method			 * the 'status' parameter is only a dummy parameter (just to show how multiple parameters can be passed)			 * if we get response from data.php, then only the cityAjax div is displayed 			 * otherwise the cityAjax div remains hidden			 * 'beforeSend' is used to display loader image			 * 'complete' is used to hide the loader image			 */						jQuery.ajax({				type: "POST",				url: "data.php",				data: ({country: optionValue, status: 1}),				beforeSend: function(){ jQuery("#ajaxLoader").show(); },				complete: function(){ jQuery("#ajaxLoader").hide(); },				success: function(response){					jQuery("#cityAjax").html(response);					jQuery("#cityAjax").show();				}			});					});	});</script><?php?>Countries: <select name="country">	<option value="">Please Select</option>	<option value="1">Nepal</option>	<option value="2">India</option>	<option value="3">China</option>	<option value="4">USA</option>	<option value="5">UK</option></select><div id="ajaxLoader" style="display:none"><img src="../ajax-loader.gif" alt="loading..."></div><div id="cityAjax" style="display:none">		<select name="city">		<option value="">Please Select</option>	</select></div>